mapper.select:
Select ID,MBR_ID,WEIGHT,HEIGHT,BMI,CREATE_TIME
From APP_PREPARATION_WEIGHT_RECORD
Where 1=1

	and ID = #{id}


	and MBR_ID = #{mbrId}

order by CREATE_TIME Desc

   Limit 0,#{num}
